// src/App.js
import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import CarouselDisplay from './components/CarouselDisplay';
import FullScreenModal from './components/FullScreenModal';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// Importar imágenes
import img1 from '../images/060924_001.jpg';
import img2 from '../images/120824_201.jpg';
import img3 from '../images/120824_236.jpg';
import img4 from '../images/120824_242.jpg';
import img5 from '../images/120824_251.jpg';
import img6 from '../images/120824_252.jpg';
import img7 from '../images/120824_267.jpg';
// ... Importa más imágenes según necesites

function App() {
    // Definir grupos de imágenes
    const groups = [
        {
            name: 'Grupo 1',
            images: [img1, img2],
        },
        {
            name: 'Grupo 2',
            images: [img3, img4],
        },
        // Añade más grupos según necesites
    ];

    const [selectedGroup, setSelectedGroup] = useState(0);
    const [showSidebar, setShowSidebar] = useState(false);
    const [modalImage, setModalImage] = useState(null);

    // Detectar si el dispositivo es móvil
    const isMobile = window.innerWidth < 768;

    // Manejar clic en una imagen para mostrar en pantalla completa
    const handleImageClick = (imgSrc) => {
        setModalImage({ src: imgSrc, width: 0, height: 0 });

        // Crear una imagen para obtener sus dimensiones
        const img = new Image();
        img.src = imgSrc;
        img.onload = () => {
            setModalImage({ src: imgSrc, width: img.width, height: img.height });
        };
    };

    // Manejar cierre del modal
    const handleCloseModal = () => {
        setModalImage(null);
    };

    return (
        <div className="App">
            {/* Botón para abrir el sidebar en móviles */}
            {isMobile && (
                <button
                    className="menu-button"
                    onClick={() => setShowSidebar(!showSidebar)}
                >
                    ☰
                </button>
            )}

            {/* Sidebar */}
            <Sidebar
                groups={groups}
                selectedGroup={selectedGroup}
                handleSelectGroup={setSelectedGroup}
                isMobile={isMobile}
                showSidebar={showSidebar}
                handleClose={() => setShowSidebar(false)}
            />

            {/* Contenedor principal */}
            <div className={`main-content ${isMobile ? 'mobile' : 'desktop'}`}>
                <CarouselDisplay
                    group={groups[selectedGroup]}
                    handleImageClick={handleImageClick}
                />
            </div>

            {/* Modal de pantalla completa */}
            <FullScreenModal
                show={modalImage !== null}
                img={modalImage}
                handleClose={handleCloseModal}
            />
        </div>
    );
}

export default App;
