import React, { useState } from 'react';

function CarouselDisplay({ images }) {
    const [currentIndex, setCurrentIndex] = useState(0);
    const totalImages = images.length;

    const goNext = () => setCurrentIndex((currentIndex + 1) % totalImages);
    const goPrev = () => setCurrentIndex((currentIndex - 1 + totalImages) % totalImages);

    return (
        <div className="carousel-container">
            {images.map((img, index) => (
                <img
                    key={index}
                    src={img}
                    alt={`Imagen ${index}`}
                    className={`carousel-image ${index === currentIndex ? 'active' : ''}`}
                    onClick={index === currentIndex ? goNext : goPrev}
                    style={{ opacity: index === currentIndex ? 1 : 0.6, transform: `scale(${index === currentIndex ? 1 : 0.8})` }}
                />
            ))}
        </div>
    );
}

export default CarouselDisplay;
