// StarField.js
import React, { useRef, useEffect } from 'react';

const StarField = ({
  numStars = 100,      // Número total de estrellas
  twinkleSpeed = 0.005, // Velocidad de parpadeo
}) => {
  const canvasRef = useRef(null);
  const stars = useRef([]);

  // Clase Star para representar cada estrella
  class Star {
    constructor(x, y, radius, color) {
      this.x = x;
      this.y = y;
      this.radius = radius;
      this.color = color;
      this.opacity = Math.random();
      this.direction = Math.random() > 0.5 ? 1 : -1;
    }

    update() {
      this.opacity += this.direction * twinkleSpeed;
      if (this.opacity <= 0) {
        this.opacity = 0;
        this.direction = 1;
      } else if (this.opacity >= 1) {
        this.opacity = 1;
        this.direction = -1;
      }
    }

    draw(ctx) {
      ctx.beginPath();
      ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
      ctx.fillStyle = `rgba(${this.color.r}, ${this.color.g}, ${this.color.b}, ${this.opacity})`;
      ctx.fill();
    }
  }

  // Inicializar estrellas
  const initializeStars = (width, height) => {
    stars.current = [];
    for (let i = 0; i < numStars; i++) {
      const x = Math.random() * width;
      const y = Math.random() * height;
      const radius = Math.random() * 1.5 + 0.5; // Tamaño de la estrella
      // Determinar el color (1% amarillo, 99% blanco)
      const isYellow = Math.random() < 0.01;
      const color = isYellow
        ? { r: 255, g: 255, b: 224 } // Amarillo claro
        : { r: 255, g: 255, b: 255 }; // Blanco
      stars.current.push(new Star(x, y, radius, color));
    }
  };

  // Manejar el redimensionamiento del canvas
  const resizeCanvas = (canvas) => {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    initializeStars(canvas.width, canvas.height);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    resizeCanvas(canvas);

    // Redimensionar el canvas al cambiar el tamaño de la ventana
    window.addEventListener('resize', () => resizeCanvas(canvas));

    let animationFrameId;

    const render = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      stars.current.forEach((star) => {
        star.update();
        star.draw(ctx);
      });
      animationFrameId = requestAnimationFrame(render);
    };

    render();

    // Cleanup on unmount
    return () => {
      cancelAnimationFrame(animationFrameId);
      window.removeEventListener('resize', () => resizeCanvas(canvas));
    };
  }, [numStars, twinkleSpeed]);

  // Estilos para que el canvas cubra toda la página
  const canvasStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'black', // Fondo negro para simular el espacio
    zIndex: -1, // Asegura que el canvas esté detrás de otros elementos
  };

  return <canvas ref={canvasRef} style={canvasStyle} />;
};

export default StarField;
