// src/components/CarouselDisplay.js
import React, { useState, useEffect } from 'react';
import './CarouselDisplay.css';

function CarouselDisplay({ group, handleImageClick }) {
    const [currentImages, setCurrentImages] = useState(group.images);
    const [animating, setAnimating] = useState(false);
    const [direction, setDirection] = useState(null);

    useEffect(() => {
        setCurrentImages(group.images);
    }, [group]);

    const handleClick = (directionClick) => {
        if (animating) return; // Evitar múltiples animaciones al mismo tiempo
        setDirection(directionClick);
        setAnimating(true);

        // Simular animación de desaparición
        setTimeout(() => {
            if (directionClick === 'left') {
                // Mover al grupo anterior
                // Aquí deberías manejar la lógica para seleccionar el grupo anterior
                // Por simplicidad, asumiremos que el grupo ya está seleccionado desde el Sidebar
                // Puedes adaptar esta lógica según tus necesidades
            } else if (directionClick === 'right') {
                // Mover al siguiente grupo
                // Similar al anterior
            }
            setAnimating(false);
        }, 500); // Duración de la animación en CSS
    };

    return (
        <div className="carousel-container">
            {group.images.map((img, index) => (
                <img
                    key={index}
                    src={img}
                    alt={`Imagen ${index + 1}`}
                    className={`carousel-image ${animating ? `fade-out-${direction}` : ''}`}
                    onClick={() => handleImageClick({ src: img, width: 0, height: 0 })}
                />
            ))}

            {/* Botones para navegar */}
            <button className="nav-button left" onClick={() => handleClick('left')}>
                &#8592;
            </button>
            <button className="nav-button right" onClick={() => handleClick('right')}>
                &#8594;
            </button>
        </div>
    );
}

export default CarouselDisplay;
