// src/components/FullScreenModal.js
import React from 'react';
import './FullScreenModal.css';

function FullScreenModal({ show, img, handleClose }) {
    if (!show) return null;

    // Determinar orientación de la imagen
    const isLandscape = img.width > img.height;

    return (
        <div className="modal-overlay" onClick={handleClose}>
            <div
                className="modal-content"
                onClick={(e) => e.stopPropagation()} // Evita que el clic se propague al overlay
                style={{
                    width: isLandscape ? '80%' : 'auto',
                    height: isLandscape ? 'auto' : '80%',
                }}
            >
                <img src={img.src} alt="Enlarged" className="modal-image" />
            </div>
        </div>
    );
}

export default FullScreenModal;
