// src/components/CarouselDisplay.js
import React, { useState } from 'react';
import './CarouselDisplay.css';

function CarouselDisplay({ group, handleImageClick, handleNavigate }) {
    const [animating, setAnimating] = useState(false);
    const [direction, setDirection] = useState(null);

    const handleClick = (dir) => {
        if (animating) return; // Evita múltiples animaciones al mismo tiempo
        setDirection(dir);
        setAnimating(true);
        handleNavigate(dir);

        // Simular animación de desaparición
        setTimeout(() => {
            setAnimating(false);
        }, 500); // Duración de la animación en CSS
    };

    const handleImageGroupClick = () => {
        handleImageClick(group.images);
    };

    return (
        <div className="carousel-container">
            {/* Botones para navegar */}
            <button className="nav-button left" onClick={() => handleClick('left')}>
                &#8592;
            </button>
            <button className="nav-button right" onClick={() => handleClick('right')}>
                &#8594;
            </button>

            {/* Imágenes */}
            <div className="images-wrapper">
                {group.images.map((img, index) => (
                    <img
                        key={index}
                        src={img}
                        alt={`Imagen ${index + 1}`}
                        className={`carousel-image ${animating ? `fade-out-${direction}` : ''}`}
                        onClick={handleImageGroupClick}
                    />
                ))}
            </div>
        </div>
    );
}

export default CarouselDisplay;
