// src/App.js
import './App.js';
import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import { Button } from 'react-bootstrap';

function App() {
  const [showSidebar, setShowSidebar] = useState(false);
  const [selectedCarousel, setSelectedCarousel] = useState(1);

  const handleSelectCarousel = (num) => {
    setSelectedCarousel(num);
  };

  const handleCloseSidebar = () => {
    setShowSidebar(false);
  };

  return (
    <div className="App">
      {/* Botón para dispositivos móviles */}
      <Button
        variant="primary"
        className="d-md-none"
        onClick={() => setShowSidebar(true)}
      >
        Menú
      </Button>

      {/* Sidebar para dispositivos de escritorio */}
      <div className="d-none d-md-block">
        <Sidebar
          show={true}
          handleSelectCarousel={handleSelectCarousel}
        />
      </div>

      {/* Sidebar para dispositivos móviles */}
      {showSidebar && (
        <Sidebar
          show={showSidebar}
          handleClose={handleCloseSidebar}
          handleSelectCarousel={handleSelectCarousel}
        />
      )}

      {/* Contenido principal */}
      <div className="content">
        {/* Aquí irán los carruseles */}
        <h1>Carrusel {selectedCarousel}</h1>
      </div>
    </div>
  );
}

export default App;
